import React from 'react';
import { useState } from 'react';

import { useEffect } from 'react';
import { useContext } from 'react';

import NavBar from '../NavBar';

import axios from 'axios'; // Import Axios
import styles from './home.module.css';
import { user_authorized } from '../functions'

import { Link } from 'react-router-dom';

const baseURL = "http://localhost:8000"; // for local dev this is the backend server

function HomePage(props) {
    const logoPath = '/images/secra_logo.png'; // in public folder
    const token = props.token;
    const isLoggedIn = user_authorized(token);

    return (
    <>
        <br />
        <br />

        <h1 className={styles.textCenter}>Welcome to Secra!</h1>

        <div className={styles.imageCenter}>
            <img src={process.env.PUBLIC_URL + logoPath} className={styles.center} alt="logo" />
        </div>

        <div className={styles.textCenter}>
            <h2>Secra is an AI scheduling assistant <span className={styles.homeLightBlueText}>for students</span>. 
            It has 3 main features: </h2>

            <br />

            <h2 className={styles.centerContainer}>
                <ul className={styles.noBullets}>
                    <li>- <span className={`${styles.homeDarkBlueText} ${styles.boldText}`}>Events:</span> Schedule events and meetings</li>
                    <li>2. <span className={`${styles.homeDarkBlueText} ${styles.boldText}`}>Canvas:</span> Plan times to work on assignments</li>
                    <li>3. <span className={`${styles.homeDarkBlueText} ${styles.boldText}`}>Connect:</span> Chat with other student assistants</li>
                </ul>
            </h2>

            { isLoggedIn ? (

            <h2>
                Thanks for signing in! Get started with <Link href="/chat">Chat</Link>.
            </h2> ):

            (<h2>
                To get started, please <Link href="/login">login</Link> or <Link href="/register">register</Link>.
            </h2>)

            }

        </div>
    </>
    );
}

export default HomePage;
